import React from "react";
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from "react-router-dom";

// Sidebar con menú
function Sidebar() {
  return (
    <div style={{ width: 250, background: "#2c3e50", color: "white", height: "100vh", padding: 20 }}>
      <h2>Rastreador de Asistencia</h2>
      <nav style={{ marginTop: 40 }}>
        <ul style={{ listStyle: "none", padding: 0 }}>
          <li><Link style={linkStyle} to="/panel">Panel de Control</Link></li>
          <li><Link style={linkStyle} to="/estudiantes">Estudiantes</Link></li>
          <li><Link style={linkStyle} to="/reportes">Reportes</Link></li>
        </ul>
      </nav>
    </div>
  );
}

const linkStyle = {
  color: "white",
  textDecoration: "none",
  display: "block",
  margin: "10px 0"
};

function PanelControl() {
  return (
    <div>
      <h1>Panel de Control</h1>
      <p>Seguimiento Diario</p>
    </div>
  );
}

function Estudiantes() {
  return (
    <div>
      <h1>Gestión de Estudiantes</h1>
      <p>Ver, agregar y gestionar estudiantes</p>
    </div>
  );
}

function Reportes() {
  return (
    <div>
      <h1>Reportes</h1>
      <p>Informes estadísticos</p>
    </div>
  );
}

function App() {
  return (
    <Router>
      <div style={{ display: "flex" }}>
        <Sidebar />
        <div style={{ padding: 20, flex: 1 }}>
          <Routes>
            <Route path="/" element={<Navigate to="/panel" />} />
            <Route path="/panel" element={<PanelControl />} />
            <Route path="/estudiantes" element={<Estudiantes />} />
            <Route path="/reportes" element={<Reportes />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
