import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from "react-router-dom";

function Sidebar() {
  return (
    <div style={{ width: 250, background: "#2c3e50", color: "white", height: "100vh", padding: 20 }}>
      <h2>Rastreador de Asistencia</h2>
      <nav style={{ marginTop: 40 }}>
        <ul style={{ listStyle: "none", padding: 0 }}>
          <li><Link style={linkStyle} to="/panel">Panel de Control</Link></li>
          <li><Link style={linkStyle} to="/estudiantes">Estudiantes</Link></li>
          <li><Link style={linkStyle} to="/reportes">Reportes</Link></li>
        </ul>
      </nav>
    </div>
  );
}

const linkStyle = {
  color: "white",
  textDecoration: "none",
  display: "block",
  margin: "10px 0",
  padding: "8px 12px",
  borderRadius: "4px",
  backgroundColor: "#34495e",
  transition: "background-color 0.3s",
};

function PanelControl() {
  const [usuario, setUsuario] = useState("");
  const [password, setPassword] = useState("");
  const [mensaje, setMensaje] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();
    // Aquí puedes validar usuario y password con backend o lógica simple
    if (usuario === "profesor" && password === "1234") {
      setMensaje("¡Bienvenido, Profesor!");
    } else {
      setMensaje("Usuario o contraseña incorrectos");
    }
  };

  return (
    <div>
      <h1>Panel de Control</h1>
      <p>Ingrese con su usuario y contraseña para acceder al sistema</p>
      <form onSubmit={handleLogin} style={{ maxWidth: 300 }}>
        <input
          type="text"
          placeholder="Usuario"
          value={usuario}
          onChange={(e) => setUsuario(e.target.value)}
          style={{ width: "100%", marginBottom: 10, padding: 8 }}
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ width: "100%", marginBottom: 10, padding: 8 }}
        />
        <button type="submit" style={{ padding: "8px 16px", cursor: "pointer" }}>
          Ingresar
        </button>
      </form>
      {mensaje && <p>{mensaje}</p>}
    </div>
  );
}

// Lista simulada de estudiantes
const estudiantesData = [
  { nombre: "Sofia Rodriguez", grado: 11, padre: "Carlos Rodriguez", email: "c.rodriguez@example.com", telefono: "+57 310-123-4567" },
  { nombre: "Mateo Gomez", grado: 10, padre: "Lucia Gomez", email: "l.gomez@example.com", telefono: "+57 311-234-5678" },
  { nombre: "Isabella Diaz", grado: 9, padre: "Juan Diaz", email: "j.diaz@example.com", telefono: "+57 312-345-6789" },
  { nombre: "Sebastian Peña", grado: 8, padre: "Ana Peña", email: "a.pena@example.com", telefono: "+57 313-456-7890" },
];

function Estudiantes() {
  return (
    <div>
      <h1>Gestión de Estudiantes</h1>
      <input
        type="text"
        placeholder="Filtrar por nombre de estudiante..."
        style={{ marginBottom: 20, padding: 8, width: "100%", maxWidth: 400 }}
        // Aquí podrías agregar estado para filtrar la lista
      />
      <table border="1" cellPadding="8" cellSpacing="0" style={{ borderCollapse: "collapse", width: "100%", maxWidth: 800 }}>
        <thead>
          <tr style={{ backgroundColor: "#ddd" }}>
            <th>Estudiante</th>
            <th>Grado</th>
            <th>Nombre del Padre/Madre</th>
            <th>Email del Padre/Madre</th>
            <th>Teléfono del Padre/Madre</th>
          </tr>
        </thead>
        <tbody>
          {estudiantesData.map((est, idx) => (
            <tr key={idx}>
              <td>{est.nombre}</td>
              <td>{est.grado}</td>
              <td>{est.padre}</td>
              <td>{est.email}</td>
              <td>{est.telefono}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

// Datos simulados para reportes
const reportesData = {
  totalAlumnos: 120,
  ausentes: 5,
  conUniforme: 110,
  sinUniforme: 10,
};

function Reportes() {
  return (
    <div>
      <h1>Reportes</h1>
      <ul>
        <li>Número total de alumnos: {reportesData.totalAlumnos}</li>
        <li>Alumnos ausentes: {reportesData.ausentes}</li>
        <li>Alumnos con uniforme: {reportesData.conUniforme}</li>
        <li>Alumnos sin uniforme: {reportesData.sinUniforme}</li>
      </ul>
    </div>
  );
}

function App() {
  return (
    <Router>
      <div style={{ display: "flex" }}>
        <Sidebar />
        <div style={{ padding: 20, flex: 1 }}>
          <Routes>
            <Route path="/" element={<Navigate to="/panel" />} />
            <Route path="/panel" element={<PanelControl />} />
            <Route path="/estudiantes" element={<Estudiantes />} />
            <Route path="/reportes" element={<Reportes />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
