import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

export default function AttendancePage() {
  const { token } = useContext(AuthContext);
  const [students, setStudents] = useState([]);

  useEffect(() => {
    // Example fetch - backend must be running
    axios.get('/api/students', { headers: { Authorization: token ? `Bearer ${token}` : '' } })
         .then(res => setStudents(res.data))
         .catch(() => setStudents([]));
  }, [token]);

  return (
    <div>
      <h1>Registro de asistencia - IED Kennedy</h1>
      <ul>
        {students.map(s => <li key={s.id}>{s.name} - {s.present ? 'Presente' : 'Ausente'}</li>)}
      </ul>
    </div>
  );
}
